css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  -webkit-filter: invert(100%);
}
img.content-dark {
  -webkit-filter: invert(100%);
}
img.content-light {
  -webkit-filter: invert(100%);
}
.mail-label {
  -webkit-filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.undo-redo-toast {
  position: absolute;
  bottom: 10px;
  z-index: 100;
  width: 100%;
  text-align: center;
  pointer-events: none;
}
.undo-redo-toast .content {
  height: 45px;
  display: inline-block;
  opacity: 0.9;
  border-radius: 3px;
  background: #dddddd;
  white-space: nowrap;
  cursor: default;
  pointer-events: initial;
  display: inline-flex;
  flex-direction: row;
  align-items: center;
  max-width: 90%;
}
.undo-redo-toast .message {
  flex: 1;
  flex-shrink: 1;
  margin-left: 16px;
  margin-right: 30px;
  color: #212121;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: left;
}
.undo-redo-toast .action {
  flex-shrink: 0;
  margin-right: 8px;
  white-space: nowrap;
  background: rgba(35, 31, 32, 0.2);
  padding: 3px 8px;
  border-radius: 4px;
  border: 1px solid rgba(33, 33, 33, 0.2);
}
.undo-redo-toast .action img {
  background-color: #212121;
}
.undo-redo-toast .action:hover {
  background: rgba(35, 31, 32, 0.3);
  border: 1px solid rgba(33, 33, 33, 0.3);
}
.undo-redo-toast .action .undo-action-text {
  margin-left: 5px;
  color: #212121;
}
body.platform-win32 .content {
  border-radius: 0;
}
.undo-redo-toast-fade-enter {
  opacity: 0.01;
  transform: translate3d(0, 10px, 0);
  transition: all 0.15s ease-out;
}
.undo-redo-toast-fade-enter.undo-redo-toast-fade-enter-active {
  opacity: 1;
  transform: translate3d(0, 0, 0);
}
.undo-redo-toast-fade-leave {
  opacity: 1;
  transform: translate3d(0, 0, 0);
  transition: all 0.15s ease-in;
}
.undo-redo-toast-fade-leave.undo-redo-toast-fade-leave-active {
  transform: translate3d(0, 10px, 0);
  opacity: 0.01;
}
.countdown {
  height: 30px;
  width: 30px;
  font-size: 0.9em;
  margin-left: 5px;
  text-align: center;
  position: relative;
}
.countdown .countdown-number {
  color: #212121;
  display: inline-block;
  line-height: 30px;
}
.countdown svg {
  position: absolute;
  top: 0;
  right: 0;
  width: 30px;
  height: 30px;
  transform: rotateY(-180deg) rotateZ(-90deg);
}
.countdown svg circle {
  stroke-dasharray: 88px;
  stroke-dashoffset: 0;
  stroke-linecap: round;
  stroke-width: 1.5px;
  stroke: #212121;
  fill: none;
  animation: countdown 10s linear forwards;
}
@keyframes countdown {
  0% {
    stroke-dashoffset: 0;
    stroke: #212121;
  }
  65% {
    stroke-dashoffset: 57.2px;
    stroke: #212121;
  }
  100% {
    stroke-dashoffset: 88px;
    stroke: #f0ad4e;
  }
}
