css:
.sheet-container .sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #51504a, #34332f);
  box-shadow: none;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back img.content-mask,
.sheet-container .sheet-toolbar .toolbar-menu-control img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-container .sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
body.is-blurred .sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #34332f, #34332f);
}
@keyframes spin {
  100% {
    -webkit-transform: rotate(-360deg);
    transform: rotate(-360deg);
  }
}
.container-identity {
  max-width: 887px;
  min-width: 530px;
  margin: auto;
}
.container-identity .id-header {
  color: rgba(35, 31, 32, 0.5);
  margin-bottom: 10px;
}
.container-identity .refresh {
  float: right;
  color: rgba(35, 31, 32, 0.5);
  margin-bottom: 10px;
}
.container-identity .refresh img {
  background-color: rgba(35, 31, 32, 0.5);
}
.container-identity .refresh.spinning img {
  animation: spin 1.4s linear infinite;
}
.container-identity .identity-content-box {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  color: rgba(35, 31, 32, 0.8);
  border-radius: 5px;
  border: 1px solid #e6e6e6;
  background-color: #f6f6f6;
}
.container-identity .identity-content-box .row {
  display: flex;
  align-items: center;
  width: 100%;
}
.container-identity .identity-content-box .padded {
  display: block;
  padding: 20px;
  border-top: 1px solid #e6e6e6;
}
.container-identity .identity-content-box .basic-explanation {
  padding-top: 12px;
}
.container-identity .identity-content-box .basic-explanation a {
  text-decoration: underline;
  color: inherit;
}
.container-identity .identity-content-box .btn {
  width: 180px;
  text-align: center;
  margin-right: 12px;
  margin-bottom: 12px;
}
.container-identity .identity-content-box .btn.minor-width {
  width: 120px;
}
.container-identity .identity-content-box .identity-actions {
  margin-top: 5px;
}
.container-identity .identity-content-box .identity-info {
  flex: 1;
  line-height: 1.9em;
}
.container-identity .identity-content-box .identity-info .name {
  font-size: 1.4em;
}
.container-identity .identity-content-box .features {
  display: flex;
  max-width: 470px;
  margin-top: 20px;
}
.container-identity .identity-content-box .features ul {
  flex: 1;
  margin: 0;
  line-height: 1.9em;
  list-style-type: none;
  padding: 0;
}
.container-identity .identity-content-box .subscription-actions {
  text-align: center;
  padding: 20px;
  background: #ffffff;
  margin-left: 50px;
  border: 1px solid #e2e2e2;
}
.container-identity .identity-content-box .subscription-actions .btn.btn-emphasis {
  margin: 0;
  line-height: 32px;
  height: 32px;
  background: linear-gradient(to bottom, #8e9fff 0%, #696dff 100%);
  border: 1px solid #7664ff;
}
.container-identity .identity-content-box .subscription-actions .btn.btn-emphasis:active {
  background: linear-gradient(to bottom, #748bfe 0%, #484efa 100%);
  border: 1px solid #6453fe;
}
.container-identity .identity-content-box .subscription-actions .pro-feature-ring {
  display: block;
  width: 205px;
  height: 205px;
  position: relative;
}
.container-identity .identity-content-box .subscription-actions .pro-feature-ring .price,
.container-identity .identity-content-box .subscription-actions .pro-feature-ring .period {
  display: block;
  z-index: 2;
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
}
.container-identity .identity-content-box .subscription-actions .pro-feature-ring .price {
  font-size: 50px;
  line-height: 50px;
  top: 40%;
}
.container-identity .identity-content-box .subscription-actions .pro-feature-ring .period {
  top: 56%;
}
.container-identity .feature-explore-title {
  font-size: 1.4em;
  margin-top: 1.8em;
}
.container-identity .feature-explore-grid {
  margin-top: 7px;
  width: 100%;
  position: relative;
}
.container-identity .feature-explore-grid .feature {
  display: inline-block;
  text-decoration: none;
  width: calc(50% - 20px);
  background: #ffffff;
  cursor: default;
  padding: 20px;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.2);
  border-radius: 4px;
  margin-right: 20px;
  margin-bottom: 20px;
  transform: translate(0, 0);
  transition: box-shadow 200ms linear, transform 200ms linear;
  vertical-align: top;
  border-left: 3px solid #f07746;
}
.container-identity .feature-explore-grid .feature h3 {
  margin-top: 0;
  font-size: 17px;
  font-weight: 500;
  display: flex;
  align-items: center;
}
.container-identity .feature-explore-grid .feature h3 img {
  vertical-align: bottom;
}
.container-identity .feature-explore-grid .feature .popout {
  display: none;
  position: absolute;
  top: 15px;
  right: 20px;
}
.container-identity .feature-explore-grid .feature:hover {
  transform: translate(0, -3px);
  box-shadow: 0 3px 3px rgba(0, 0, 0, 0.2);
}
.container-identity .feature-explore-grid .feature:hover .popout {
  display: inline-block;
}
@media (max-width: 600px) {
  .container-identity .features {
    flex-direction: column;
  }
}
