css:
.sheet-container .sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #51504a, #34332f);
  box-shadow: none;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back img.content-mask,
.sheet-container .sheet-toolbar .toolbar-menu-control img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-container .sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
body.is-blurred .sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #34332f, #34332f);
}
.thread-sharing-button {
  order: -102;
}
.thread-sharing-button.active img.content-mask {
  background-color: #f07441;
}
.fixed-popover .thread-sharing-popover {
  position: relative;
  width: 280px;
}
.fixed-popover .thread-sharing-popover .share-toggle {
  padding: 7px 10px;
}
.fixed-popover .thread-sharing-popover .share-toggle .meta {
  font-size: 12.04px;
  padding-left: 19px;
}
.fixed-popover .thread-sharing-popover .share-toggle input {
  margin-right: 7px;
}
.fixed-popover .thread-sharing-popover .share-input {
  border-top: 1px solid rgba(0, 0, 0, 0.15);
  padding: 10px;
  padding-bottom: 0;
}
.fixed-popover .thread-sharing-popover .share-input input[type='text'] {
  cursor: default;
  -webkit-user-select: all;
}
.fixed-popover .thread-sharing-popover .share-controls {
  padding: 10px;
}
.fixed-popover .thread-sharing-popover .share-controls button.btn + button.btn {
  margin-left: 10px;
}
.fixed-popover .thread-sharing-popover.disabled .share-input input[type='text'] {
  color: rgba(35, 31, 32, 0.5);
  -webkit-user-select: none;
}
.fixed-popover .thread-sharing-popover.disabled button.btn {
  color: rgba(35, 31, 32, 0.5);
}
