css:
.sheet-container .sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #51504a, #34332f);
  box-shadow: none;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back img.content-mask,
.sheet-container .sheet-toolbar .toolbar-menu-control img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-container .sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
body.is-blurred .sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #34332f, #34332f);
}
.undo-redo-toast {
  position: absolute;
  bottom: 10px;
  z-index: 100;
  width: 100%;
  text-align: center;
  pointer-events: none;
}
.undo-redo-toast .content {
  height: 45px;
  display: inline-block;
  opacity: 0.9;
  border-radius: 3px;
  background: #292d32;
  white-space: nowrap;
  cursor: default;
  pointer-events: initial;
  display: inline-flex;
  flex-direction: row;
  align-items: center;
  max-width: 90%;
}
.undo-redo-toast .message {
  flex: 1;
  flex-shrink: 1;
  margin-left: 16px;
  margin-right: 30px;
  color: #ffffff;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: left;
}
.undo-redo-toast .action {
  flex-shrink: 0;
  margin-right: 8px;
  white-space: nowrap;
  background: rgba(35, 31, 32, 0.2);
  padding: 3px 8px;
  border-radius: 4px;
  border: 1px solid rgba(255, 255, 255, 0.2);
}
.undo-redo-toast .action img {
  background-color: #ffffff;
}
.undo-redo-toast .action:hover {
  background: rgba(35, 31, 32, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.3);
}
.undo-redo-toast .action .undo-action-text {
  margin-left: 5px;
  color: #ffffff;
}
body.platform-win32 .content {
  border-radius: 0;
}
.undo-redo-toast-fade-enter {
  opacity: 0.01;
  transform: translate3d(0, 10px, 0);
  transition: all 0.15s ease-out;
}
.undo-redo-toast-fade-enter.undo-redo-toast-fade-enter-active {
  opacity: 1;
  transform: translate3d(0, 0, 0);
}
.undo-redo-toast-fade-leave {
  opacity: 1;
  transform: translate3d(0, 0, 0);
  transition: all 0.15s ease-in;
}
.undo-redo-toast-fade-leave.undo-redo-toast-fade-leave-active {
  transform: translate3d(0, 10px, 0);
  opacity: 0.01;
}
.countdown {
  height: 30px;
  width: 30px;
  font-size: 0.9em;
  margin-left: 5px;
  text-align: center;
  position: relative;
}
.countdown .countdown-number {
  color: #ffffff;
  display: inline-block;
  line-height: 30px;
}
.countdown svg {
  position: absolute;
  top: 0;
  right: 0;
  width: 30px;
  height: 30px;
  transform: rotateY(-180deg) rotateZ(-90deg);
}
.countdown svg circle {
  stroke-dasharray: 88px;
  stroke-dashoffset: 0;
  stroke-linecap: round;
  stroke-width: 1.5px;
  stroke: #ffffff;
  fill: none;
  animation: countdown 10s linear forwards;
}
@keyframes countdown {
  0% {
    stroke-dashoffset: 0;
    stroke: #ffffff;
  }
  65% {
    stroke-dashoffset: 57.2px;
    stroke: #ffffff;
  }
  100% {
    stroke-dashoffset: 88px;
    stroke: #f0ad4e;
  }
}
