css:
.sheet-container .sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #51504a, #34332f);
  box-shadow: none;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back img.content-mask,
.sheet-container .sheet-toolbar .toolbar-menu-control img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-container .sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
body.is-blurred .sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #34332f, #34332f);
}
body.platform-win32 .category-picker-popover {
  margin-left: 0;
}
.sheet-toolbar .btn-category-picker:only-of-type {
  margin-right: 0;
}
.category-picker-popover .menu,
.category-picker-dropdown .menu {
  background: #f6f6f6;
  width: 250px;
  max-height: 400px;
}
.category-picker-popover .menu .header-container,
.category-picker-dropdown .menu .header-container {
  border-bottom: 0;
}
.category-picker-popover .menu .item.divider,
.category-picker-dropdown .menu .item.divider {
  background-color: #e0e0e0;
  margin: 4px 0;
  height: 2px;
  padding: 0;
}
.category-picker-popover .btn.btn-toolbar,
.category-picker-dropdown .btn.btn-toolbar {
  margin-left: 0;
  margin-right: 0;
}
.category-picker-popover .check-wrap,
.category-picker-dropdown .check-wrap {
  width: 14px;
  height: 14px;
  border-radius: 2px;
  display: inline-block;
  position: relative;
  flex-shrink: 0;
  top: 2px;
}
.category-picker-popover .item img.content-mask,
.category-picker-dropdown .item img.content-mask {
  background-color: rgba(35, 31, 32, 0.8);
}
.category-picker-popover .item.selected img.content-mask,
.category-picker-dropdown .item.selected img.content-mask,
.category-picker-popover .item.active img.content-mask,
.category-picker-dropdown .item.active img.content-mask {
  background-color: #ffffff;
}
.category-picker-popover img.check-img,
.category-picker-dropdown img.check-img {
  position: absolute;
}
.category-picker-popover .category-item,
.category-picker-dropdown .category-item {
  font-size: 14px;
  display: flex;
  align-items: center;
}
.category-picker-popover .category-create-new-tag,
.category-picker-dropdown .category-create-new-tag {
  flex-shrink: 0;
}
.category-picker-popover .category-display-name,
.category-picker-dropdown .category-display-name {
  display: inline-block;
  margin-left: 10px;
  margin-right: 5px;
  word-break: break-word;
  flex: 1;
}
