css:
.sheet-container .sheet-toolbar .btn-toolbar img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #51504a, #34332f);
  box-shadow: none;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn {
  background: #4c4b45;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .btn.btn-toolbar {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .sheet-toolbar-container .toolbar-activity .activity-toolbar-icon {
  background: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back img.content-mask,
.sheet-container .sheet-toolbar .toolbar-menu-control img.content-mask {
  background-color: #f6f6f6;
}
.sheet-container .sheet-toolbar .item-back .item-back-title {
  color: #f6f6f6;
}
.sheet-container .sheet-toolbar .selection-bar .absolute {
  border-color: #4e4d47;
  background-color: #2c2b28;
}
.sheet-container .sheet-toolbar .selection-bar .absolute .inner .centered {
  color: #f6f6f6;
}
.btn-icon img.content-mask {
  background-color: #f6f6f6;
  color: #f6f6f6;
}
.btn-icon img.content-mask:hover {
  background-color: #f07746;
}
body.is-blurred .sheet-container .sheet-toolbar .sheet-toolbar-container {
  background-image: -webkit-linear-gradient(top, #34332f, #34332f);
}
.notifications {
  background-color: #f6f7f8;
  box-shadow: 0 -6px 4px #f6f7f8;
  z-index: 2;
}
.notification {
  background: #009ec4;
  display: none;
  color: #ffffff;
  margin: 10px;
  margin-top: 0;
  border-radius: 5px;
}
.notification.error {
  background: #ca2541;
}
.notification.offline {
  background: linear-gradient(to top, #b88a00 0%, #cc9900 100%);
}
.notification.highest-priority {
  display: block;
}
.notif-top {
  display: flex;
  align-items: flex-start;
  padding: 10px;
}
.notification .icon {
  margin-right: 10px;
}
.notification .title {
  padding: 10px;
}
.notification .subtitle {
  font-size: 12.04px;
  position: relative;
  opacity: 0.8;
}
.notification .subtitle.has-action {
  cursor: pointer;
}
.notification .subtitle.has-action::after {
  content: '';
  background: url(mailspring://notifications/assets/minichevron@2x.png) top left no-repeat;
  background-size: 4.5px 7px;
  margin-left: 3px;
  display: inline-block;
  width: 4.5px;
  height: 7px;
  vertical-align: baseline;
}
.notification .actions-wrapper {
  display: flex;
}
.notification .action {
  text-align: center;
  flex: 1;
  border-top: solid rgba(255, 255, 255, 0.5) 1px;
  border-left: solid rgba(255, 255, 255, 0.5) 1px;
  padding: 10px;
  cursor: pointer;
  /* The semi-transparent backgrounds that can be layered on top
     of this class shouldn't have sharp corners on the bottom */
  border-bottom-left-radius: 5px;
  border-bottom-right-radius: 5px;
}
.notification .action:first-child {
  border-left: none;
}
.notification .action:hover {
  background-color: rgba(255, 255, 255, 0.2);
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.15) inset;
}
.notification .action.loading {
  cursor: progress;
  background-color: rgba(0, 0, 0, 0.2);
  box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.15) inset;
}
